definition.script:
#!groovy
def chef_repo = "/vagrant/chef-repo"
def chef_recipe = "run_container"
def git_repo = "ksandrmatveyev/gradle-training.git"
def git_branch = "task7_test"
def jenkins_url = "http://172.20.20.31:8080"
node('master'){
    stage('Check Params'){
        echo ENVS
        echo RLS
        echo VRS
        sh "knife search \"chef_environment:${ENVS} and role:${RLS}\""
    }
    stage('Update Cookbook Files'){
        sh "sed -i '/\"${chef_recipe}\":/c\\\"${chef_recipe}\": \"= $VRS\"' ${chef_repo}/environments/${ENVS}.json"
        sh "sed -i \"/version /c\\version \'\"$VRS\"\'\" ${chef_repo}/cookbooks/${chef_recipe}/metadata.rb"
        sh "sed -i \"/image_version/c\\default['image_version']=\'\"$VRS\"\'\" ${chef_repo}/cookbooks/${chef_recipe}/attributes/default.rb"
        sh "cat ${chef_repo}/environments/${ENVS}.json"
        sh "cat ${chef_repo}/cookbooks/${chef_recipe}/metadata.rb"
        sh "cat ${chef_repo}/cookbooks/${chef_recipe}/attributes/default.rb"
    }
    stage('Update Chef-server'){
        sh "berks install -b ${chef_repo}/cookbooks/${chef_recipe}/Berksfile && berks upload -b ${chef_repo}/cookbooks/${chef_recipe}/Berksfile"
        sh "knife environment from file ${chef_repo}/environments/${ENVS}.json"
    }
    stage('Push to Github'){
        withCredentials([usernamePassword(credentialsId: 'github_cred', passwordVariable: 'GIT_PASSWORD', usernameVariable: 'GIT_USERNAME')]) {
            git branch: "${git_branch}", credentialsId: 'github_cred', url: "https://github.com/${git_repo}"
            withCredentials([usernamePassword(credentialsId: 'jenkins_cred', passwordVariable: 'JENKINS_PASSWORD', usernameVariable: 'JENKINS_USERNAME')]) {
                sh "curl -u $JENKINS_USERNAME:$JENKINS_PASSWORD ${jenkins_url}/job/${env.JOB_NAME}/config.xml > ${env.JOB_NAME}.xml"
            }
            sh "cp -R ${chef_repo} ."
            sh('git config --global user.name "Aleksandr Matveyev"')
            sh('git config --global user.email ksandr.matveyev@gmail.com')
            sh("git add -A && git commit -am \"from Jenkins. Cookbook build - ${VRS}\"")
            sh("git push https://${GIT_USERNAME}:${GIT_PASSWORD}@github.com/${git_repo} ${git_branch}") 
        }
    }
    stage('Run Chef-client'){
        withCredentials([usernamePassword(credentialsId: 'node_cred', passwordVariable: 'NODE_PASSWORD', usernameVariable: 'NODE_USERNAME')]) {
            sh "knife search \"chef_environment:${ENVS} and role:${RLS}\" -i"
            sh "knife ssh \"chef_environment:${ENVS} AND role:${RLS}\" \"sudo chef-client\" -x ${NODE_USERNAME} -P ${NODE_PASSWORD}"
        }
    }
}
